package package_June08;

public class MyActivity05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double width = 5.6;
		double height = 8.5;
		
		System.out.print("Area is 5.6*8.5 = ");
		System.out.format("%.2f%n" , (width*height));
		
		System.out.print("Perimeter is 2*(5.6+8.5) = ");
		System.out.format("%.2f%n" , 2*(width + height));      
	}

}
